import { useState, useEffect } from "react";
import axios from "../api/axios";
import GameForm from "../components/GameForm";
import GameList from "../components/GameList";

const Home = () => {
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [foundGames, setFoundGames] = useState([]);
  const [searchError, setSearchError] = useState("");

  const fetchGames = async () => {
    const res = await axios.get("/games");
    setGames(res.data);
  };

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;

    try {
      const res = await axios.get(`/games/search?name=${searchTerm}`);
      if (res.data.length > 0) {
        setFoundGames(res.data);
        setSearchError("");
      } else {
        setFoundGames([]);
        setSearchError("Game not found");
      }
    } catch (err) {
      setFoundGames([]);
      setSearchError("Error searching for games");
    }
  };

  const handleResetSearch = () => {
    setSearchTerm("");
    setFoundGames([]);
    setSearchError("");
  };

  useEffect(() => {
    fetchGames();
  }, []);

  return (
    <div>
      <h1>Game CRUD App</h1>

      <div style={{ marginBottom: "1rem" }}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={handleResetSearch}>Reset</button>
      </div>

      {searchError && <p style={{ color: "red" }}>{searchError}</p>}

      {foundGames.length > 0 && (
        <div
          style={{
            border: "1px solid #ccc",
            padding: "1rem",
            marginBottom: "1rem",
          }}
        >
          <h3>Search Results ({foundGames.length} found):</h3>
          <ul>
            {foundGames.map((game) => (
              <li key={game._id}>
                <p>
                  <strong>{game.name}</strong> (ID: {game._id})
                </p>
                <p>Author: {game.author}</p>
                <a href={game.url} target="_blank" rel="noreferrer">
                  Visit
                </a>
              </li>
            ))}
          </ul>
        </div>
      )}

      <GameForm
        fetchGames={fetchGames}
        selectedGame={selectedGame}
        setSelectedGame={setSelectedGame}
      />

      <GameList
        games={games}
        fetchGames={fetchGames}
        setSelectedGame={setSelectedGame}
      />
    </div>
  );
};

export default Home;
