import { useState } from 'react';
import {
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Typography,
  Card,
  CardContent,
  Box,
  Snackbar,
} from '@mui/material';
import { Edit, Delete } from '@mui/icons-material';
import LanguageIcon from '@mui/icons-material/Language';
import axios from '../api/axios';

const GameList = ({ games, fetchGames, setSelectedGame }) => {
  const [editMessage, setEditMessage] = useState('');

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/games/${id}`);
      fetchGames();
    } catch (error) {
      console.error("Error deleting the game:", error);
    }
  };

  const handleEditClick = (game) => {
    setSelectedGame(game);
    setEditMessage('Edit in edit section');
    // Hide message after 3 seconds
    setTimeout(() => setEditMessage(''), 3000);
  };

  return (
    <>
      {/* Snackbar for Edit Message */}
      <Snackbar
        open={editMessage !== ''}
        message={editMessage}
        autoHideDuration={3000}
        onClose={() => setEditMessage('')}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      />

      <Card sx={{ maxWidth: 600, margin: 'auto', padding: 3, borderRadius: 2, boxShadow: 3 }}>
        <CardContent>
          <Typography variant="h6" gutterBottom sx={{ textAlign: 'center', fontSize: '18px' }}>
            All Games
          </Typography>

          <List>
            {games.map((game) => (
              <ListItem
                key={game._id}
                divider
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  borderRadius: '10px',
                  px: 2,
                  py: 1,
                }}
              >
                <ListItemText
                  primary={<strong>{game.name}</strong>}
                  secondary={`By ${game.author} | Game ID: ${game._id}`}
                  sx={{ fontSize: '16px' }}
                />

                <ListItemSecondaryAction>
                  <Box display="flex" alignItems="center">
                    {/* Visit Button */}
                    <IconButton
                      edge="end"
                      href={game.url}
                      target="_blank"
                      rel="noreferrer"
                      sx={{
                        color: '#4CAF50',
                        backgroundColor: '#e8f5e9',
                        borderRadius: '5px',
                        marginRight: 1,
                        padding: '6px',
                        '&:hover': {
                          backgroundColor: '#c8e6c9',
                        },
                      }}
                    >
                      <LanguageIcon fontSize="small" />
                    </IconButton>

                    {/* Edit Button */}
                    <IconButton
                      edge="end"
                      onClick={() => handleEditClick(game)}
                      sx={{
                        color: '#4CAF50',
                        backgroundColor: '#e8f5e9',
                        borderRadius: '5px',
                        marginRight: 1,
                        padding: '6px',
                        '&:hover': {
                          backgroundColor: '#c8e6c9',
                        },
                      }}
                    >
                      <Edit fontSize="small" />
                    </IconButton>

                    {/* Delete Button */}
                    <IconButton
                      edge="end"
                      onClick={() => handleDelete(game._id)}
                      sx={{
                        color: '#f44336',
                        backgroundColor: '#ffebee',
                        borderRadius: '5px',
                        padding: '6px',
                        '&:hover': {
                          backgroundColor: '#ffcdd2',
                        },
                      }}
                    >
                      <Delete fontSize="small" />
                    </IconButton>
                  </Box>
                </ListItemSecondaryAction>
              </ListItem>
            ))}
          </List>
        </CardContent>
      </Card>
    </>
  );
};

export default GameList;
