import { useState, useEffect } from 'react';
import axios from '../api/axios';
import GameForm from '../components/GameForm';
import GameList from '../components/GameList';
import GameSearch from '../components/GameSearch';

import {
  Container,
  Typography,
  Box,
} from '@mui/material';

const Home = () => {
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);

  // Fetch all games on initial load
  const fetchGames = async () => {
    try {
      const res = await axios.get('/games');
      setGames(res.data);
    } catch (err) {
      console.error('Error fetching games:', err);
    }
  };

  useEffect(() => {
    fetchGames();
  }, []);

  return (
    <Container maxWidth="100%" sx={{ py: 4 }}>
      {/* Title at the top left corner */}
      <Box sx={{ display: 'flex', justifyContent: 'flex-start' }}>
        <Typography variant="h4" gutterBottom>
          TimePass Game Logbook
        </Typography>
      </Box>

      {/* Flex container to align GameForm, GameList, and GameSearch in a row */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', gap: 4, mt: 3 }}>
        {/* Game Form on the left */}
        <Box sx={{ flex: 1, pr: 2 }}>
          <GameForm
            fetchGames={fetchGames}
            selectedGame={selectedGame}
            setSelectedGame={setSelectedGame}
          />
        </Box>

        {/* Game List in the center */}
        <Box sx={{ flex: 2, px: 2 }}>
          <GameList
            games={games}
            fetchGames={fetchGames}
            setSelectedGame={setSelectedGame}
          />
        </Box>

        {/* Game Search on the right */}
        <Box sx={{ flex: 1, pl: 2 }}>
          <GameSearch setGames={setGames} />
        </Box>
      </Box>

      {/* Footer at the bottom */}
      <Box sx={{ mt: 4, textAlign: 'center', py: 2, borderTop: '1px solid #ccc' }}>
        <Typography variant="body2" color="textSecondary">
          Â© 2025 TimePass. All rights reserved.
        </Typography>
      </Box>
    </Container>
  );
};

export default Home;
