import { useState, useEffect } from 'react';
import axios from '../api/axios';
import GameForm from '../components/GameForm';
import GameList from '../components/GameList';

const Home = () => {
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [foundGame, setFoundGame] = useState(null);
  const [searchError, setSearchError] = useState('');

  const fetchGames = async () => {
    const res = await axios.get('/games');
    setGames(res.data);
  };

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;

    try {
      const res = await axios.get(`/games/search?name=${searchTerm}`);
      if (res.data.length > 0) {
        setFoundGame(res.data[0]); // Display only first match
        setSearchError('');
      } else {
        setFoundGame(null);
        setSearchError('Game not found');
      }
    } catch (err) {
      setFoundGame(null);
      setSearchError('Error searching for game');
    }
  };

  const handleResetSearch = () => {
    setSearchTerm('');
    setFoundGame(null);
    setSearchError('');
  };

  useEffect(() => {
    fetchGames();
  }, []);

  return (
    <div>
      <h1>Game CRUD App</h1>

      <div style={{ marginBottom: '1rem' }}>
        <input
          type="text"
          placeholder="Search by name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
        <button onClick={handleResetSearch}>Reset</button>
      </div>

      {searchError && <p style={{ color: 'red' }}>{searchError}</p>}

      {foundGame && (
        <div style={{ border: '1px solid #ccc', padding: '1rem', marginBottom: '1rem' }}>
          <h3>Search Result:</h3>
          <p><strong>{foundGame.name}</strong> by {foundGame.author}</p>
          <p><strong>Game ID:</strong> {game._id}</p>
          <a href={foundGame.url} target="_blank" rel="noreferrer">Visit</a>
        </div>
      )}

      <GameForm
        fetchGames={fetchGames}
        selectedGame={selectedGame}
        setSelectedGame={setSelectedGame}
      />

      <GameList
        games={games}
        fetchGames={fetchGames}
        setSelectedGame={setSelectedGame}
      />
    </div>
  );
};

export default Home;
