import { List, ListItem, ListItemText, ListItemSecondaryAction, IconButton, Typography, Card, CardContent } from '@mui/material';
import { Edit, Delete } from '@mui/icons-material';
import axios from '../api/axios';

const GameList = ({ games, fetchGames, setSelectedGame }) => {
  // Handle game deletion
  const handleDelete = async (id) => {
    try {
      // Send delete request to API
      await axios.delete(`/games/${id}`);
      fetchGames(); // Re-fetch the updated list of games after deletion
    } catch (error) {
      console.error("Error deleting the game:", error); // Log any errors during deletion
    }
  };

  return (
    <Card sx={{ maxWidth: 600, margin: 'auto', padding: 2, borderRadius: 2, boxShadow: 3 }}>
      <CardContent>
        <Typography variant="h6" gutterBottom sx={{ textAlign: 'center', fontSize: '18px' }}>
          All Games
        </Typography>
        <List>
          {/* Iterate over the games array and display each game */}
          {games.map((game) => (
            <ListItem key={game._id} divider sx={{ display: 'flex', alignItems: 'center' }}>
              <ListItemText
                primary={<strong>{game.name}</strong>}
                secondary={`By ${game.author} | Game ID: ${game._id}`}
                sx={{ fontSize: '16px' }} // Consistent font size for ListItemText
              />
              <ListItemSecondaryAction sx={{ display: 'flex', gap: 1 }}>
                {/* Edit Button */}
                <IconButton 
                  edge="end" 
                  onClick={() => setSelectedGame(game)} 
                  sx={{ fontSize: '20px', color: 'primary.main' }} // IconButton styling
                >
                  <Edit />
                </IconButton>
                {/* Delete Button */}
                <IconButton 
                  edge="end" 
                  onClick={() => handleDelete(game._id)} 
                  sx={{ fontSize: '20px', color: 'error.main' }} // IconButton styling
                >
                  <Delete />
                </IconButton>
              </ListItemSecondaryAction>
            </ListItem>
          ))}
        </List>
      </CardContent>
    </Card>
  );
};

export default GameList;
