import { List, ListItem, ListItemText, ListItemSecondaryAction, IconButton, Typography, Button } from '@mui/material';
import { Edit, Delete } from '@mui/icons-material';
import axios from '../api/axios';

const GameList = ({ games, fetchGames, setSelectedGame }) => {
  // Handle game deletion
  const handleDelete = async (id) => {
    try {
      // Send delete request to API
      await axios.delete(`/games/${id}`);
      fetchGames(); // Re-fetch the updated list of games after deletion
    } catch (error) {
      console.error("Error deleting the game:", error); // Log any errors during deletion
    }
  };

  return (
    <div>
      <Typography variant="h6" gutterBottom>
        All Games
      </Typography>
      <List>
        {/* Iterate over the games array and display each game */}
        {games.map((game) => (
          <ListItem key={game._id} divider>
            <ListItemText
              primary={<strong>{game.name}</strong>}
              secondary={`By ${game.author} | Game ID: ${game._id}`}
            />
            <ListItemSecondaryAction>
              {/* Edit Button */}
              <IconButton edge="end" onClick={() => setSelectedGame(game)}>
                <Edit />
              </IconButton>
              {/* Delete Button */}
              <IconButton edge="end" onClick={() => handleDelete(game._id)}>
                <Delete />
              </IconButton>
            </ListItemSecondaryAction>
          </ListItem>
        ))}
      </List>
    </div>
  );
};

export default GameList;
