import { useState } from 'react';
import axios from '../api/axios';
import { TextField, Button, Box, Card, CardContent, Typography, Alert, Paper, List, ListItem, Link } from '@mui/material';

const GameSearch = ({ setGames }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [foundGames, setFoundGames] = useState([]);
  const [searchError, setSearchError] = useState('');

  const handleSearch = async () => {
    if (!searchTerm.trim()) return; // Prevent search if the searchTerm is empty

    try {
      const res = await axios.get(`/games/search?name=${searchTerm}`);
      
      if (res.data.length > 0) {
        setFoundGames(res.data);
        setSearchError('');
      } else {
        setFoundGames([]);
        setSearchError('Game not found');
      }
    } catch (error) {
      setFoundGames([]);
      setSearchError('Error searching for games');
      console.error("Error searching for game:", error);
    }
  };

  const handleResetSearch = () => {
    setSearchTerm('');
    setFoundGames([]);
    setSearchError('');
  };

  return (
    <Card sx={{ maxWidth: 600, margin: 'auto', padding: 3, borderRadius: 2, boxShadow: 3 }}>
      <CardContent>
        <Typography variant="h6" gutterBottom sx={{ textAlign: 'center', fontSize: '18px' }}>
          Search Games
        </Typography>
        <Box display="flex" alignItems="center" gap={2}>
          {/* MUI TextField for searching games by name */}
          <TextField
            label="Search game by name"
            variant="outlined"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)} // Update the search term on change
            fullWidth
            sx={{ fontSize: '16px' }} // Consistent font size for the TextField
          />
          
          {/* MUI Button triggers the handleSearch function */}
          <Button
            variant="contained"
            color="primary"
            onClick={handleSearch}
            sx={{ fontSize: '16px', padding: '10px 20px' }} // Consistent font size and padding for the button
          >
            Search
          </Button>
          
          {/* Reset Button */}
          <Button
            variant="outlined"
            onClick={handleResetSearch}
            sx={{ fontSize: '16px', padding: '10px 20px' }}
          >
            Reset
          </Button>
        </Box>

        {/* Error Alert */}
        {searchError && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {searchError}
          </Alert>
        )}

        {/* Search Results */}
        {foundGames.length > 0 && (
          <Paper sx={{ p: 2, mb: 3 }} elevation={3}>
            <Typography variant="h6">
              Search Results ({foundGames.length} found)
            </Typography>
            <List>
              {foundGames.map((game) => (
                <ListItem key={game._id} divider>
                  <Typography>
                    <strong>{game.name}</strong> by {game.author}{' '}
                    (<strong>ID: {game._id}</strong>) –{' '}
                    <Link href={game.url} target="_blank" rel="noreferrer">
                      Visit
                    </Link>
                  </Typography>
                </ListItem>
              ))}
            </List>
          </Paper>
        )}
      </CardContent>
    </Card>
  );
};

export default GameSearch;
