import { useState, useEffect } from 'react';
import axios from '../api/axios';
import GameForm from '../components/GameForm';
import GameList from '../components/GameList';
import GameSearch from '../components/GameSearch';

import {
  Box,
  Typography,
} from '@mui/material';

const Home = () => {
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);

  // Fetch all games on initial load
  const fetchGames = async () => {
    try {
      const res = await axios.get('/games');
      setGames(res.data);
    } catch (err) {
      console.error('Error fetching games:', err);
    }
  };

  useEffect(() => {
    fetchGames();
  }, []);

  return (
    <Box sx={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {/* Title at the top left corner */}
      <Box sx={{ padding: 2 }}>
        <Typography variant="h4" gutterBottom>
          TimePass Game Logbook
        </Typography>
      </Box>

      {/* Centered GameSearch component */}
      <Box sx={{ display: 'flex', justifyContent: 'center', padding: 2 }}>
        <GameSearch setGames={setGames} />
      </Box>

      {/* Flex container for GameForm and GameList side by side */}
      <Box sx={{ display: 'flex', flexGrow: 1, paddingX: 2 }}>
        {/* Game Form on the left */}
        <Box sx={{ flex: 1, paddingRight: 2 }}>
          <GameForm
            fetchGames={fetchGames}
            selectedGame={selectedGame}
            setSelectedGame={setSelectedGame}
          />
        </Box>

        {/* Game List in the center */}
        <Box sx={{ flex: 2, paddingLeft: 2 }}>
          <GameList
            games={games}
            fetchGames={fetchGames}
            setSelectedGame={setSelectedGame}
          />
        </Box>
      </Box>

      {/* Footer at the bottom, fixed */}
      <Box sx={{ textAlign: 'center', py: 2, borderTop: '1px solid #ccc', position: 'absolute', bottom: 0, width: '100%', backgroundColor: 'white' }}>
        <Typography variant="body2" color="textSecondary">
          Â© 2025 TimePass. All rights reserved.
        </Typography>
      </Box>
    </Box>
  );
};

export default Home;
