import { useState, useEffect } from 'react';
import axios from '../api/axios';
import GameForm from '../components/GameForm';
import GameList from '../components/GameList';

import {
  Container,
  Typography,
  TextField,
  Button,
  Box,
  Alert,
  Paper,
  List,
  ListItem,
  Link,
} from '@mui/material';

const Home = () => {
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [foundGames, setFoundGames] = useState([]);
  const [searchError, setSearchError] = useState('');

  const fetchGames = async () => {
    try {
      const res = await axios.get('/games');
      setGames(res.data);
    } catch (err) {
      console.error('Error fetching games:', err);
    }
  };

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;

    try {
      const res = await axios.get(`/games/search?name=${searchTerm}`);
      if (res.data.length > 0) {
        setFoundGames(res.data);
        setSearchError('');
      } else {
        setFoundGames([]);
        setSearchError('Game not found');
      }
    } catch (err) {
      setFoundGames([]);
      setSearchError('Error searching for games');
    }
  };

  const handleResetSearch = () => {
    setSearchTerm('');
    setFoundGames([]);
    setSearchError('');
  };

  useEffect(() => {
    fetchGames();
  }, []);

  return (
    <Container maxWidth="xl" sx={{ py: 4 }}>
      <Typography variant="h4" gutterBottom>
        TimePass Game Logbook
      </Typography>

      {/* Search Section */}
      <Box display="flex" gap={2} alignItems="center" mb={3} flexDirection="column" alignItems="flex-start">
        <TextField
          label="Search by name"
          variant="outlined"
          fullWidth
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <Box mt={2} display="flex" gap={2}>
          <Button variant="contained" onClick={handleSearch}>
            Search
          </Button>
          <Button variant="outlined" onClick={handleResetSearch}>
            Reset
          </Button>
        </Box>
      </Box>

      {/* Error Alert */}
      {searchError && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {searchError}
        </Alert>
      )}

      {/* Search Results */}
      {foundGames.length > 0 && (
        <Paper sx={{ p: 2, mb: 3 }} elevation={3}>
          <Typography variant="h6">
            Search Results ({foundGames.length} found)
          </Typography>
          <List>
            {foundGames.map((game) => (
              <ListItem key={game._id} divider>
                <Typography>
                  <strong>{game.name}</strong> by {game.author}{' '}
                  (<strong>ID: {game._id}</strong>) –{' '}
                  <Link href={game.url} target="_blank" rel="noreferrer">
                    Visit
                  </Link>
                </Typography>
              </ListItem>
            ))}
          </List>
        </Paper>
      )}

      {/* Main Section with Flex Layout */}
      <Box display="flex" justifyContent="center" gap={3} flexDirection="row" flexWrap="wrap">
        {/* Game Form on the Left */}
        <Box sx={{ width: { xs: '100%', sm: '30%' }, mb: 3 }}>
          <GameForm
            fetchGames={fetchGames}
            selectedGame={selectedGame}
            setSelectedGame={setSelectedGame}
          />
        </Box>

        {/* Game List in the Center */}
        <Box sx={{ width: { xs: '100%', sm: '40%' }, mb: 3 }}>
          <GameList
            games={games}
            fetchGames={fetchGames}
            setSelectedGame={setSelectedGame}
          />
        </Box>

        {/* Search Results on the Right */}
        <Box sx={{ width: { xs: '100%', sm: '30%' }, mb: 3 }}>
          {foundGames.length > 0 && (
            <Paper sx={{ p: 2 }} elevation={3}>
              <Typography variant="h6">
                Search Results ({foundGames.length} found)
              </Typography>
              <List>
                {foundGames.map((game) => (
                  <ListItem key={game._id} divider>
                    <Typography>
                      <strong>{game.name}</strong> by {game.author}{' '}
                      (<strong>ID: {game._id}</strong>) –{' '}
                      <Link href={game.url} target="_blank" rel="noreferrer">
                        Visit
                      </Link>
                    </Typography>
                  </ListItem>
                ))}
              </List>
            </Paper>
          )}
        </Box>
      </Box>
    </Container>
  );
};

export default Home;
