import { useEffect, useState } from 'react';
import axios from '../api/axios';
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Divider,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import GameForm from './GameForm';

const GameDashboard = () => {
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);

  const fetchGames = async () => {
    try {
      const res = await axios.get('/games');
      setGames(res.data);
    } catch (err) {
      console.error('Error fetching games:', err);
    }
  };

  const handleDelete = async (gameId) => {
    const confirmDelete = window.confirm('Are you sure you want to delete this game?');
    if (!confirmDelete) return;

    try {
      await axios.delete(`/games/${gameId}`);
      fetchGames();
    } catch (err) {
      console.error('Error deleting game:', err);
    }
  };

  const handleEdit = (game) => {
    setSelectedGame(game);
  };

  useEffect(() => {
    fetchGames();
  }, []);

  return (
    <Box sx={{ padding: 4 }}>
      <Box
        sx={{
          display: 'flex',
          alignItems: 'center',
          gap: 2,
          marginBottom: 3,
        }}
      >
        <img src="/logo.png" alt="Logo" width={50} height={50} />
        <Typography variant="h4">TimePass Game Logbook</Typography>
      </Box>

      <GameForm
        fetchGames={fetchGames}
        selectedGame={selectedGame}
        setSelectedGame={setSelectedGame}
      />

      <Card sx={{ marginTop: 4, padding: 2 }}>
        <CardContent>
          <Typography variant="h6" gutterBottom>
            Game List
          </Typography>
          <List>
            {games.map((game) => (
              <Box key={game._id}>
                <ListItem
                  secondaryAction={
                    <>
                      <IconButton onClick={() => handleEdit(game)} edge="end" sx={{ mr: 1 }}>
                        <EditIcon />
                      </IconButton>
                      <IconButton onClick={() => handleDelete(game._id)} edge="end" color="error">
                        <DeleteIcon />
                      </IconButton>
                    </>
                  }
                >
                  <ListItemText
                    primary={game.name}
                    secondary={`Author: ${game.author}, Published: ${new Date(
                      game.publishedDate
                    ).toLocaleDateString()}`}
                  />
                </ListItem>
                <Divider />
              </Box>
            ))}
          </List>
        </CardContent>
      </Card>

      <Box sx={{ textAlign: 'center', marginTop: 6, paddingTop: 2, borderTop: '1px solid #ccc' }}>
        <Typography variant="body2">
          © {new Date().getFullYear()} TimePass Games — All rights reserved.
        </Typography>
        <Typography variant="body2">kartikpohane0612@gmail.com</Typography>
      </Box>
    </Box>
  );
};

export default GameDashboard;
