import { useEffect, useState } from 'react';
import axios from '../api/axios';
import {
  Box,
  Typography,
  Card,
  CardContent,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Divider,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Button,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import GameForm from './GameForm';

const GameDashboard = () => {
  const [games, setGames] = useState([]);
  const [selectedGame, setSelectedGame] = useState(null);
  const [gameToDelete, setGameToDelete] = useState(null);
  const [openConfirm, setOpenConfirm] = useState(false);

  const fetchGames = async () => {
    try {
      const res = await axios.get('/games');
      setGames(res.data);
    } catch (err) {
      console.error('Error fetching games:', err);
    }
  };

  const handleDelete = async () => {
    if (!gameToDelete) return;

    try {
      await axios.delete(`/games/${gameToDelete._id}`);
      fetchGames();
      handleCloseConfirm();
    } catch (err) {
      console.error('Error deleting game:', err);
    }
  };

  const handleEdit = (game) => {
    setSelectedGame(game);
  };

  const handleOpenConfirm = (game) => {
    setGameToDelete(game);
    setOpenConfirm(true);
  };

  const handleCloseConfirm = () => {
    setGameToDelete(null);
    setOpenConfirm(false);
  };

  useEffect(() => {
    fetchGames();
  }, []);

  return (
    <Box sx={{ padding: 4 }}>
      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, marginBottom: 3 }}>
        <img src="/logo.png" alt="Logo" width={50} height={50} />
        <Typography variant="h4">TimePass Game Logbook</Typography>
      </Box>

      <GameForm
        fetchGames={fetchGames}
        selectedGame={selectedGame}
        setSelectedGame={setSelectedGame}
      />

      <Card sx={{ marginTop: 4, padding: 2 }}>
        <CardContent>
          <Typography variant="h6" gutterBottom>
            Game List
          </Typography>
          <List>
            {games.map((game) => (
              <Box key={game._id}>
                <ListItem
                  secondaryAction={
                    <>
                      <IconButton onClick={() => handleEdit(game)} edge="end" sx={{ mr: 1 }}>
                        <EditIcon />
                      </IconButton>
                      <IconButton onClick={() => handleOpenConfirm(game)} edge="end" color="error">
                        <DeleteIcon />
                      </IconButton>
                    </>
                  }
                >
                  <ListItemText
                    primary={game.name}
                    secondary={`Author: ${game.author}, Published: ${new Date(
                      game.publishedDate
                    ).toLocaleDateString()}`}
                  />
                </ListItem>
                <Divider />
              </Box>
            ))}
          </List>
        </CardContent>
      </Card>

      {/* Confirm Delete Dialog */}
      <Dialog open={openConfirm} onClose={handleCloseConfirm}>
        <DialogTitle>Confirm Delete</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Are you sure you want to delete the game{' '}
            <strong>{gameToDelete?.name}</strong>? This action cannot be undone.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseConfirm} color="primary">
            Cancel
          </Button>
          <Button onClick={handleDelete} color="error" variant="contained">
            Delete
          </Button>
        </DialogActions>
      </Dialog>

      <Box sx={{ textAlign: 'center', marginTop: 6, paddingTop: 2, borderTop: '1px solid #ccc' }}>
        <Typography variant="body2">
          © {new Date().getFullYear()} TimePass Games — All rights reserved.
        </Typography>
        <Typography variant="body2">kartikpohane0612@gmail.com</Typography>
      </Box>
    </Box>
  );
};

export default GameDashboard;
