import { useState, useEffect } from 'react';
import axios from '../api/axios';
import {
  TextField,
  Button,
  Box,
  Typography,
  Card,
  CardContent,
} from '@mui/material';
import { StaticDatePicker } from '@mui/x-date-pickers/StaticDatePicker';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import dayjs from 'dayjs';

const GameForm = ({ fetchGames, selectedGame, setSelectedGame }) => {
  const [form, setForm] = useState({
    name: '',
    url: '',
    author: '',
    publishedDate: null, // Initialize as null or dayjs object
  });

  useEffect(() => {
    if (selectedGame) setForm({ ...selectedGame, publishedDate: dayjs(selectedGame.publishedDate) });
  }, [selectedGame]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleDateChange = (newDate) => {
    setForm({ ...form, publishedDate: newDate });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (selectedGame) {
      await axios.put(`/games/${selectedGame._id}`, form);
    } else {
      await axios.post('/games', form);
    }

    setForm({ name: '', url: '', author: '', publishedDate: null });
    setSelectedGame(null);
    fetchGames();
  };

  return (
    <Card
      sx={{
        maxWidth: 600,
        margin: 'auto',
        padding: 3,
        borderRadius: 2,
        boxShadow: 3,
      }}
    >
      <CardContent>
        <Typography
          variant="h6"
          gutterBottom
          sx={{ textAlign: 'center', fontSize: '18px' }}
        >
          {selectedGame ? 'Update Game' : 'Add New Game'}
        </Typography>

        <Box
          component="form"
          onSubmit={handleSubmit}
          display="flex"
          flexDirection="column"
          alignItems="center"
          gap={1.5}
          mt={2}
        >
          <TextField
            label="Game Name"
            name="name"
            value={form.name}
            onChange={handleChange}
            fullWidth
            required
            variant="outlined"
            sx={{
              fontSize: '16px',
              borderRadius: '20px',
              '& .MuiOutlinedInput-root': {
                borderRadius: '20px',
              },
            }}
          />

          <TextField
            label="Game URL"
            name="url"
            value={form.url}
            onChange={handleChange}
            fullWidth
            required
            variant="outlined"
            sx={{
              fontSize: '16px',
              borderRadius: '20px',
              '& .MuiOutlinedInput-root': {
                borderRadius: '20px',
              },
            }}
          />

          <TextField
            label="Author"
            name="author"
            value={form.author}
            onChange={handleChange}
            fullWidth
            required
            variant="outlined"
            sx={{
              fontSize: '16px',
              borderRadius: '20px',
              '& .MuiOutlinedInput-root': {
                borderRadius: '20px',
              },
            }}
          />

          {/* Date Picker Component */}
          <LocalizationProvider dateAdapter={AdapterDayjs}>
            <StaticDatePicker
              displayStaticWrapperAs="desktop"
              openTo="day"
              value={form.publishedDate}
              onChange={handleDateChange}
              renderInput={(params) => (
                <TextField
                  {...params}
                  label="Published Date"
                  required
                  fullWidth
                  variant="outlined"
                  sx={{
                    fontSize: '16px',
                    borderRadius: '20px',
                    '& .MuiOutlinedInput-root': {
                      borderRadius: '20px',
                    },
                  }}
                />
              )}
            />
          </LocalizationProvider>

          <Button
            variant="contained"
            type="submit"
            sx={{
              backgroundColor: '#4CAF50',
              color: '#fff',
              borderRadius: '5px',
              padding: '6px 16px',
              fontSize: '14px',
              minWidth: '100px',
              '&:hover': {
                backgroundColor: '#45a049',
              },
            }}
          >
            {selectedGame ? 'Update' : 'Add'} Game
          </Button>
        </Box>
      </CardContent>
    </Card>
  );
};

export default GameForm;
