import { useState, useEffect } from 'react';
import axios from '../api/axios';
import {
  TextField,
  Button,
  Box,
  Typography,
  Card,
  CardContent,
} from '@mui/material';
import { DesktopDatePicker } from '@mui/x-date-pickers/DesktopDatePicker';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import dayjs from 'dayjs';

const GameForm = ({ fetchGames, selectedGame, setSelectedGame }) => {
  const [form, setForm] = useState({
    name: '',
    url: '',
    author: '',
    publishedDate: dayjs(),
  });

  useEffect(() => {
    if (selectedGame) {
      setForm({
        ...selectedGame,
        publishedDate: dayjs(selectedGame.publishedDate),
      });
    }
  }, [selectedGame]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleDateChange = (newDate) => {
    setForm({ ...form, publishedDate: newDate });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.publishedDate || !form.publishedDate.isValid()) {
      alert('Please select a valid published date');
      return;
    }

    const gameData = {
      ...form,
      publishedDate: form.publishedDate.format('YYYY-MM-DD'),
    };

    if (selectedGame) {
      await axios.put(`/games/${selectedGame._id}`, gameData);
    } else {
      await axios.post('/games', gameData);
    }

    setForm({ name: '', url: '', author: '', publishedDate: dayjs() });
    setSelectedGame(null);
    fetchGames();
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        backgroundColor: '#f9f9f9',
        paddingBottom: '60px', // space for footer
      }}
    >
      <Card
        sx={{
          maxWidth: 600,
          margin: '40px auto',
          padding: 3,
          borderRadius: 2,
          boxShadow: 3,
        }}
      >
        <CardContent>
          <Typography
            variant="h6"
            gutterBottom
            sx={{ textAlign: 'center', fontSize: '18px' }}
          >
            {selectedGame ? 'Update Game' : 'Add New Game'}
          </Typography>

          <Box
            component="form"
            onSubmit={handleSubmit}
            display="flex"
            flexDirection="column"
            alignItems="center"
            gap={1.5}
            mt={2}
          >
            <TextField
              label="Game Name"
              name="name"
              value={form.name}
              onChange={handleChange}
              fullWidth
              required
              variant="outlined"
              sx={{
                fontSize: '16px',
                borderRadius: '20px',
                '& .MuiOutlinedInput-root': {
                  borderRadius: '20px',
                },
              }}
            />

            <TextField
              label="Game URL"
              name="url"
              value={form.url}
              onChange={handleChange}
              fullWidth
              required
              variant="outlined"
              sx={{
                fontSize: '16px',
                borderRadius: '20px',
                '& .MuiOutlinedInput-root': {
                  borderRadius: '20px',
                },
              }}
            />

            <TextField
              label="Author"
              name="author"
              value={form.author}
              onChange={handleChange}
              fullWidth
              required
              variant="outlined"
              sx={{
                fontSize: '16px',
                borderRadius: '20px',
                '& .MuiOutlinedInput-root': {
                  borderRadius: '20px',
                },
              }}
            />

            <LocalizationProvider dateAdapter={AdapterDayjs}>
              <DesktopDatePicker
                inputFormat="MM/DD/YYYY"
                value={form.publishedDate}
                onChange={handleDateChange}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    label="Published Date"
                    required
                    fullWidth
                    variant="outlined"
                    sx={{
                      fontSize: '16px',
                      borderRadius: '20px',
                      '& .MuiOutlinedInput-root': {
                        borderRadius: '20px',
                      },
                    }}
                  />
                )}
              />
            </LocalizationProvider>

            <Button
              variant="contained"
              type="submit"
              sx={{
                backgroundColor: '#4CAF50',
                color: '#fff',
                borderRadius: '5px',
                padding: '6px 16px',
                fontSize: '14px',
                minWidth: '100px',
                '&:hover': {
                  backgroundColor: '#45a049',
                },
              }}
            >
              {selectedGame ? 'Update' : 'Add'} Game
            </Button>
          </Box>
        </CardContent>
      </Card>

      {/* Footer */}
      <Box
        component="footer"
        sx={{
          position: 'fixed',
          bottom: 0,
          left: 0,
          width: '100%',
          bgcolor: '#222',
          color: '#fff',
          py: 1.5,
          textAlign: 'center',
          fontSize: '14px',
          zIndex: 1000,
        }}
      >
        © {new Date().getFullYear()} TimePass Games. All rights reserved. |{' '}
        <a
          href="mailto:kartikpohane0612@gmail.com"
          style={{ color: '#90caf9', textDecoration: 'none' }}
        >
          kartikpohane0612@gmail.com
        </a>
      </Box>
    </Box>
  );
};

export default GameForm;
