import { useState } from 'react';
import axios from '../api/axios';
import {
  TextField,
  Button,
  Box,
  Card,
  CardContent,
  Typography,
  Alert,
  Paper,
  List,
  ListItem,
  Link,
} from '@mui/material';

const GameSearch = ({ setGames }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [foundGames, setFoundGames] = useState([]);
  const [searchError, setSearchError] = useState('');

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;

    try {
      const res = await axios.get(`/games/search?name=${searchTerm}`);

      if (res.data.length > 0) {
        setFoundGames(res.data);
        setSearchError('');
      } else {
        setFoundGames([]);
        setSearchError('Game not found');
      }
    } catch (error) {
      setFoundGames([]);
      setSearchError('Error searching for games');
      console.error("Error searching for game:", error);
    }
  };

  const handleResetSearch = () => {
    setSearchTerm('');
    setFoundGames([]);
    setSearchError('');
  };

  return (
    <Card sx={{ maxWidth: 600, margin: 'auto', padding: 3, borderRadius: 2, boxShadow: 3 }}>
      <CardContent>
        <Typography variant="h6" gutterBottom sx={{ textAlign: 'center', fontSize: '18px' }}>
          Search Games
        </Typography>

        <Box
  display="flex"
  flexDirection="column"
  alignItems="center"
  gap={1.5}
  mt={2}
>
  <TextField
    label="Search game by name"
    variant="outlined"
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
    fullWidth
    sx={{
      fontSize: '16px',
      borderRadius: '20px',
      '& .MuiOutlinedInput-root': {
        borderRadius: '20px',
      },
    }}
  />

  <Box display="flex" justifyContent="center" gap={1.5}>
    <Button
      variant="contained"
      onClick={handleSearch}
      sx={{
        backgroundColor: '#4CAF50',
        color: '#fff',
        borderRadius: '5px',
        padding: '6px 16px',
        fontSize: '14px',
        minWidth: '100px',
        '&:hover': {
          backgroundColor: '#45a049',
        },
      }}
    >
      Search
    </Button>

    <Button
      variant="outlined"
      onClick={handleResetSearch}
      sx={{
        borderColor: '#4CAF50',
        color: '#4CAF50',
        borderRadius: '5px',
        padding: '6px 16px',
        fontSize: '14px',
        minWidth: '100px',
        '&:hover': {
          borderColor: '#45a049',
          color: '#45a049',
        },
      }}
    >
      Reset
    </Button>
  </Box>
</Box>

        {searchError && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {searchError}
          </Alert>
        )}

        {foundGames.length > 0 && (
          <Paper sx={{ p: 2, mt: 3 }} elevation={3}>
            <Typography variant="h6">
              Search Results ({foundGames.length} found)
            </Typography>
            <List>
              {foundGames.map((game) => (
                <ListItem key={game._id} divider>
                  <Typography>
                    <strong>{game.name}</strong> by {game.author}{' '}
                    (<strong>ID: {game._id}</strong>) â€“{' '}
                    <Link href={game.url} target="_blank" rel="noreferrer">
                      Visit
                    </Link>
                  </Typography>
                </ListItem>
              ))}
            </List>
          </Paper>
        )}
      </CardContent>
    </Card>
  );
};

export default GameSearch;
