import { useState, useEffect } from 'react';
import axios from '../api/axios';
import {
  TextField,
  Button,
  Box,
  Typography,
  Card,
  CardContent
} from '@mui/material';

const GameForm = ({ fetchGames, selectedGame, setSelectedGame }) => {
  const [form, setForm] = useState({
    name: '',
    url: '',
    author: '',
    publishedDate: '',
  });

  useEffect(() => {
    if (selectedGame) setForm(selectedGame);
  }, [selectedGame]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (selectedGame) {
      await axios.put(`/games/${selectedGame._id}`, form);
    } else {
      await axios.post('/games', form);
    }
    setForm({ name: '', url: '', author: '', publishedDate: '' });
    setSelectedGame(null);
    fetchGames();
  };

  return (
    <Card
      sx={{
        maxWidth: 400,
        margin: 'auto',
        padding: 3,
        borderRadius: 3,
        boxShadow: 3,
        mt: 3
      }}
    >
      <CardContent>
        <Typography variant="h6" gutterBottom sx={{ textAlign: 'center', fontSize: '18px' }}>
          {selectedGame ? 'Update Game' : 'Add New Game'}
        </Typography>

        <Box
          component="form"
          onSubmit={handleSubmit}
          sx={{
            display: 'flex',
            flexDirection: 'column',
            gap: 2,
            alignItems: 'center'
          }}
        >
          <TextField
            label="Game Name"
            name="name"
            value={form.name}
            onChange={handleChange}
            fullWidth
            required
            variant="outlined"
            size="small"
          />

          <TextField
            label="Game URL"
            name="url"
            value={form.url}
            onChange={handleChange}
            fullWidth
            required
            variant="outlined"
            size="small"
          />

          <TextField
            label="Author"
            name="author"
            value={form.author}
            onChange={handleChange}
            fullWidth
            required
            variant="outlined"
            size="small"
          />

          <TextField
            label="Published Date"
            type="date"
            name="publishedDate"
            value={form.publishedDate}
            onChange={handleChange}
            fullWidth
            required
            variant="outlined"
            size="small"
            InputLabelProps={{
              shrink: true,
            }}
          />

          <Button
            variant="contained"
            color="primary"
            type="submit"
            sx={{
              px: 4,
              py: 1,
              mt: 1,
              borderRadius: 2,
              textTransform: 'none',
              fontSize: '14px',
              backgroundColor: '#1976d2',
              '&:hover': {
                backgroundColor: '#1565c0',
              }
            }}
          >
            {selectedGame ? 'Update' : 'Add'} Game
          </Button>
        </Box>
      </CardContent>
    </Card>
  );
};

export default GameForm;
